mapper.select:
SELECT P_SUM_AMOUNT FROM PROJECT JOIN PRJ_CATE USING(PC_NO) WHERE SYSDATE > P_END_DATE AND P_SUM_AMOUNT > P_TARGET_AMOUNT
AND PC_NO = ${cate}
AND P_TYPE = ${type}
AND TO_CHAR(P_END_DATE, 'YYYY-MM-DD') >= '${sDate}'
AND '${eDate}' >= TO_CHAR(P_END_DATE, 'YYYY-MM-DD')

SELECT TO_CHAR(P_END_DATE, 'YYYY-MM-DD') P_END_DATE, COUNT(P_END_DATE) COUNT, SUM(P_SUM_AMOUNT) AMOUNT, SUM(P_SUM_AMOUNT * 0.05) REVENUE FROM PROJECT WHERE SYSDATE > P_END_DATE AND P_SUM_AMOUNT > P_TARGET_AMOUNT
AND PC_NO = ${cate}
AND P_TYPE = ${type}
AND TO_CHAR(P_END_DATE, 'YYYY-MM-DD') >= '${sDate}'
AND '${eDate}' >= TO_CHAR(P_END_DATE, 'YYYY-MM-DD')
GROUP BY TO_CHAR(P_END_DATE, 'YYYY-MM-DD')
